#include <iostream>
using namespace std;
#include "Client.h"

Client::Client(){
  descClient=0;
  nom="";
  argent=0.0;
}
Client::Client(int desc){
  descClient=desc;
  nom="";
  argent=0.0;
}
Client::Client(int desc, string nomClient,float argentReel){
  descClient=desc;
  nom=nomClient;
  argent=argentReel;
}




int Client::getDescClient()const{
  return descClient;
}
string Client::getNom()const{
  return nom;
}
float Client::getArgent()const{
  return argent;
}

